// import ContactUs from '../components/ContactUs';


const Contact = () => {

const form = useRef();
  const handleCombinedSubmit = async (e) => {
      e.preventDefault();

      sendEmail(e);           // Send email via EmailJS first
      await handleSubmit(e);  // Then handle the form logic (e.g., show message, reset form)
    }
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_x8yyacq', 'template_c4fqyun', form.current, {
        publicKey: 'Ige6zuCqFXM_pSemu',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    doctor: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const doctors = [
    "Select a Doctor",
    "Dr. Ahmed Khan (Cardiology)",
    "Dr. Priya Sharma (Gynaecology)",
    "Dr. Rajesh Patel (Orthopedics)",
    "Dr. Meera Desai (Pediatrics)",
    "Dr. Vikram Singh (Neurology)"
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // This would normally be an API call to your backend
      // For demo purposes, we'll simulate the email sending
      console.log('Email would be sent to palleharsha455@gmail.com with:', formData);

      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      setSubmitMessage('Message sent successfully! We will contact you soon.');

      // Reset form
      setFormData({
        name: '',
        email: '',
        subject: '',
        doctor: '',
        message: ''
      });

      // Clear success message after 5 seconds
      setTimeout(() => {
        setSubmitMessage('');
      }, 5000);
    } catch (error) {
      setSubmitMessage('Error sending message. Please try again later.');
    } finally {
      setIsSubmitting(false);
    }


  };

  return (
    <div className="alavi_contact_root_container">
      <div className="alavi_contact_inner_wrapper">
        <div className="alavi_contact_left_section">
          <h2 className="alavi_contact_section_title">Contact Information</h2>

          <div className="alavi_contact_address_group">
            <h3 className="alavi_contact_subtitle">Hyderabad Branch 1</h3>
            <p className="alavi_contact_address_line">12, 234, Adarsh Nagar,</p>
            <p className="alavi_contact_address_line">Adjeetpura Nagar, Opp., IDPL</p>
            <p className="alavi_contact_address_line">Colony, Balanagar,</p>
            <p className="alavi_contact_address_line">Secunderabad, Hyderabad,</p>
            <p className="alavi_contact_address_line">Telangana 500037</p>
          </div>

          <div className="alavi_contact_address_group">
            <h3 className="alavi_contact_subtitle">Hyderabad Branch 2</h3>
            <p className="alavi_contact_address_line">5-120/2, Jeedimedia Main</p>
            <p className="alavi_contact_address_line">Road, HMT Rd, opposite Asian</p>
            <p className="alavi_contact_address_line">sha theater, Shiva Nagar,</p>
            <p className="alavi_contact_address_line">Chinthal, Hyderabad,</p>
            <p className="alavi_contact_address_line">Telangana 500054</p>
          </div>

          <div className="alavi_contact_contact_info">
            <h3 className="alavi_contact_subtitle">Email & Phone</h3>
            <p className="alavi_contact_email">
              <strong>info@demohospitals.com</strong>
            </p>
            <p className="alavi_contact_support_text">
              Need support? Drop us an email
            </p>

            <div className="alavi_contact_phone_numbers">
              <p className="alavi_contact_phone">095331 21257</p>
              <p className="alavi_contact_phone">9160 606 108</p>
              <p className="alavi_contact_phone">040-49 99 49 49</p>
            </div>
          </div>
        </div>

        <div className="alavi_contact_right_section">
          <h2 className="alavi_contact_section_title">Send us a Message</h2>

          <form className="alavi_contact_form" ref={form} onSubmit={handleCombinedSubmit}>
            <div className="alavi_contact_form_group">
              <input
                type="text"
                name="user_name"
                value={formData.name}
                onChange={handleChange}
                className="alavi_contact_form_input"
                placeholder="Enter your name"
                required
              />
            </div>

            <div className="alavi_contact_form_group">
              <input
                type="email"
                name="user_email"
                value={formData.email}
                onChange={handleChange}
                className="alavi_contact_form_input"
                placeholder="Enter email address"
                required
              />
            </div>

            {/* <div className="alavi_contact_form_group">
              <input
                type="text"
                name="subject"
                value={formData.subject}
                onChange={handleChange}
                className="alavi_contact_form_input"
                placeholder="Enter subject"
                required
              />
            </div> */}

            <div className="alavi_contact_form_group">
              <select
                name="doctor_name"
                value={formData.doctor}
                onChange={handleChange}
                className="alavi_contact_form_select"
                required
              >
                {doctors.map((doc, index) => (
                  <option key={index} value={doc}>{doc}</option>
                ))}
              </select>
            </div>

            <div className="alavi_contact_form_group">
              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                className="alavi_contact_form_textarea"
                placeholder="Enter Message"
                rows="5"
                required
              ></textarea>
            </div>

            <button 
              type="submit" 
              value="Send"
              className="alavi_contact_submit_button"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Sending...' : 'SUBMIT'}
            </button>

            {submitMessage && (
              <div className={`alavi_contact_submit_message ${submitMessage.includes('success') ? 'success' : 'error'}`}>
                {submitMessage}
              </div>
            )}
          </form>
        </div>
      </div>
    <div>
      {/* <ContactUs /> */}
    </div>
    </div>
  );
};

export default Contact;
